select m.merchant_code, m.merchant,md.id,d.dept,md.department_1,md.department_2
from merchantdepartments md
inner join Departments d on d.id_dept = md.DepartmentID and d.Id_langue = 1
inner join merchants m on m.id = md.merchant_id
where m.merchant_code in ('BSCNAT','BSHBRK','BSHDLY','BSHCLM','BSHFWT','BSHSTB','ABSDSR','ABSPRE','ABSPLN','ABSJGR','ABSRIC','BSHFTZ','BSHGBR','BSHGRN','BSHHMB','BSHMCL','BSHRND','BSHVNC','KMBLNS','ABSDHV','ABSGGR','ABSOUE','ABSPQN','ABSSTJ','KANDER','KARVAA','KBARBE','KGROVE','KMDLND','KMDWST','KLEDUC','KBAKER','KHALTO','KMACAR','KMODER','KTOWNE','BSHVTH','BSHFRN','BSHKML','BSHKLB','BSHMCD','KFWBLK','KREDBA','KSASKT','KWARDE','KWIENS','KALPHA','KASHDN','KCLDWL','KMATUS','KNTHHW','KATLHM','KBARRA','KMERIT','KNEWAG','KBONAN','KINSPI','KM2000','KNTHFU','KRSZLL','nbworr','NBMACH','NBUNAP','NBCAST','NBORIG','NBCNYN','NBBERT','NBCRWN','NBHMGD','NBLZBL','NBOLDM','KOTTOS','BSHSDL','KHRVYS','KWELHM','KESTVN','GLARIV','NBPSSN','NBGUER','BSCTST','KTRADE','NBBDGT','BSHCBT','bshjhn','KBRADS','KYRMTH','KSFNTC','KCHRST','KASPEN','KBEARS','KYEGBD','KGANDR','KEDSON','KKMPTV','KARDRI','KDNTWN','BSHHRS','NBMDVT','NBLLND','NBBBRN','NBSLPN','ALPHAS','BARHAS','NBBUSH','K5STAR','ABSSJD','ABSDGN','ABSBVT','NBTAST','ABSSVG','GRNTST','KFRNGL','NBTMAL','NBTNGY','NBJONA','NBCOXE')